import React, { Component } from 'react';
import Home from './components/Home.js';
import About from './components/About.js';
import Loading from './components/Loading.js';
import { Experiences } from './components/Experiences.js';
import { Contact } from './components/Contact.js';
import { Element } from 'react-scroll';
import styled from 'styled-components';

const Styles = styled.div`
  #content {
    background-color: white;
    position: relative;
    height: 100vh;
    width: 100vw;
    max-width: None;
    z-index: 5;
  }

  #loading {
    display: 'none';
  }
`;

class App extends Component {
  constructor() {
    super();
    this.state = {
      content: React.createRef(),
      loading: React.createRef()
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.state.content.current.style = {
        display: 'block'
      }
      this.state.loading.current.style = {
        display: 'none'
      }
    }, 1000)
  }


  render() {
    return (
      <Styles>
        <div ref={this.state.loading} id="loading" style={{display: 'block'}}>
          <Loading />
        </div>
        <div ref={this.state.content} id="loaded" style={{display: 'none'}}>
          <React.Fragment>
            <Element name='home' id='home'>
              <Home />
            </Element>
            <div id="content">
              <Element name='about' id='about'>
                <About />
              </Element>
              <Element name='experiences' id='experiences'>
                <Experiences />
              </Element>
              <Element name='contact' id='contact'>
                <Contact />
              </Element>
            </div>
          </React.Fragment>
        </div>
      </Styles>
    );
  }
}

export default App;
