import React, { Component } from 'react';
import { AboutJumbotron } from './Jumbotron.js';
import styled from 'styled-components';
import { Image } from 'react-bootstrap';
import AboutIMG from '../assets/me-space-1.png';
import { CSSTransition } from "react-transition-group";
import { Waypoint } from 'react-waypoint';
const Styles = styled.div`
  @import url('https://fonts.googleapis.com/css2?family=Raleway:wght@300&display=swap');

  #about-container {
    font-family: "Raleway", sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 85vh;
  }

  #me-img {
    width: 30vw;
    min-width: 150px;
    margin: 10px;
    border: solid #8cc4ec 1px;
  }

  #about-content {
    max-height: 75vh;
    width: 60vw;
    margin: 10px 10px 10px 15px;
    overflow: scroll;
    font-size: 1.5rem;
  }

    /* appear - on page load */

  .enter {
      opacity: 0;
      z-index: 1;
      transform: scale(0.97) translateY(5px);
  }

  .enter.enter-active {
    opacity: 1;
    transform: scale(1) translateY(0);
    transition: opacity 500ms linear 500ms, transform 500ms ease-in-out 500ms;
  }
`;

class About extends Component {
  constructor(props) {
    super(props);
    this.state = {
      appearAbout: false
    }
  }

  waypointEnter() {
    console.log("hi");
    this.setState({ appearAbout: true });
    console.log(this.state);
  }

  render() {
    const { appearAbout } = this.state;
    return (
      <Styles>
        <Waypoint
          onEnter={this.waypointEnter}
        />

        <CSSTransition
          in={true}
          enter={true}
          timeout={2000}
          clasNames="fade"
        >
          <div>
            <AboutJumbotron />
            <div id="about-container">
              <div id="image-container">
                <Image id="me-img" src={AboutIMG} roundedCircle/>
              </div>
              <div id="about-content">
                <p>
                Hi! My name is Ethan. I am currently a student at the University of Washington with a passion for Computer Science.  Right now my interests include full-stack web development and database management.  So far I've worked on a variety of projects on and off campus, and am excited to get involved in more!
                </p>
              </div>
            </div>
          </div>
        </CSSTransition>
      </Styles>
    )
  }
}

export default About;
